$(document).ready(function(){function t(){$(".box").each(function(){r=Math.floor(256*Math.random()),g=Math.floor(256*Math.random()),b=Math.floor(256*Math.random()),$(this).css("background-color","rgb("+r+","+g+","+b+")")})}for(var o=60,a=document.getElementById("empty"),e=0;o>e;e++)a.innerHTML+='<div class="box"></div>';t();var n=setInterval(t,500);setInterval(function(){$(".box").animate({height:700,width:700,"border-radius":"50%"},1e4),$(".box").animate({height:0,width:0,"border-radius":"0%"},1e4)})});